(function(pluginName, message) {
  tinymce.PluginManager.add(pluginName, function(editor) {
    editor.on("skinLoaded", function() {
      editor.notificationManager.open({
        text: message,
        type: 'warning'
      });
    });
  });
})("mentions", "The mentions premium plugin is not enabled on your API key. \u003ca target=\"_blank\" href=\"https://www.tiny.cloud/pricing/\"\u003eUpgrade your account\u003c/a\u003e.");
